import axios from 'axios';

export const api = axios.create({
  baseURL: 'http://localhost/tanzania/php-backend/public/api',
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json'
  },
  withCredentials: true
});

// Add a request interceptor
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Add a response interceptor
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// Auth API
export const authApi = {
  login: (credentials) => api.post('/auth/login', credentials),
  logout: () => api.post('/auth/logout'),
  me: () => api.get('/auth/me'),
};

// Package Management
export const packageApi = {
  getAll: () => api.get('/packages'),
  getById: (id) => api.get(`/packages/${id}`),
  create: (data) => api.post('/packages', data),
  update: (id, data) => api.put(`/packages/${id}`, data),
  delete: (id) => api.delete(`/packages/${id}`),
  updateImage: (id, formData) => api.post(`/packages/${id}/image`, formData, {
    headers: {
      'Content-Type': 'multipart/form-data',
    },
  })
};

// Category Management
export const categoryApi = {
  getAll: () => api.get('/categories'),
  getById: (id) => api.get(`/categories/${id}`),
  create: (data) => api.post('/categories', data),
  update: (id, data) => api.put(`/categories/${id}`, data),
  delete: (id) => api.delete(`/categories/${id}`)
};

// Park Management
export const parkApi = {
  getAll: () => api.get('/parks'),
  getById: (id) => api.get(`/parks/${id}`),
  create: (data) => {
    console.log('Making API request to create park:', data);
    return api.post('/parks', data);
  },
  update: (id, data) => api.put(`/parks/${id}`, data),
  delete: (id) => api.delete(`/parks/${id}`),
  updateImage: (id, formData) => api.post(`/parks/${id}/image`, formData, {
    headers: { 'Content-Type': 'multipart/form-data' }
  })
};

// Booking Management
export const bookingApi = {
  getAll: () => api.get('/bookings'),
  getById: (id) => api.get(`/bookings/${id}`),
  update: (id, data) => api.put(`/bookings/${id}`, data),
  delete: (id) => api.delete(`/bookings/${id}`),
  updateStatus: (id, status) => api.patch(`/bookings/${id}/status`, { status })
};

// Enquiry Management
export const enquiryApi = {
  getAll: () => api.get('/enquiries'),
  getById: (id) => api.get(`/enquiries/${id}`),
  update: (id, data) => api.put(`/enquiries/${id}`, data),
  delete: (id) => api.delete(`/enquiries/${id}`),
  updateStatus: (id, status) => api.patch(`/enquiries/${id}/status`, { status })
};

// Settings Management
export const settingsApi = {
  get: () => api.get('/settings'),
  update: (data) => api.put('/settings', data)
};
