import React, { useState, useEffect } from 'react';
import { FaPlus, FaEdit, FaTrash, FaList } from 'react-icons/fa';
import Layout from '../components/layout/Layout';
import PageHeader from '../components/common/PageHeader';
import { categoryApi } from '../services/api';
import DeleteConfirmation from '../components/common/DeleteConfirmation';
import CategoryForm from '../components/forms/CategoryForm';
// import '../styles/AdminPages.css';
import './Categories.css';

const Categories = () => {
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('name');
  const [sortOrder, setSortOrder] = useState('asc');
  const [filteredCategories, setFilteredCategories] = useState([]);

  const fetchCategories = async () => {
    try {
      const response = await categoryApi.getAll();
      setCategories(response.data?.data || []);
    } catch (error) {
      setError('Failed to fetch categories');
      console.error('Error fetching categories:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCategories();
  }, []);

  const handleCreate = async (data) => {
    try {
      await categoryApi.create(data);
      fetchCategories();
      setShowForm(false);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleUpdate = async (id, data) => {
    try {
      await categoryApi.update(id, data);
      fetchCategories();
      setShowForm(false);
      setSelectedCategory(null);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleDelete = async (id) => {
    try {
      await categoryApi.delete(id);
      fetchCategories();
      setShowDeleteModal(false);
      setSelectedCategory(null);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSort = (field) => {
    if (field === sortBy) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(field);
      setSortOrder('asc');
    }
  };

  if (loading) {
    return (
      <Layout>
        <div className="flex items-center justify-center min-h-screen">
          <div className="loading-spinner" />
        </div>
      </Layout>
    );
  }

  if (error) {
    return (
      <Layout>
        <div className="p-4">
          <div className="bg-red-50 text-red-700 p-4 rounded-lg">
            {error}
          </div>
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="min-h-screen bg-gray-50 p-6">
        <div className="max-w-7xl mx-auto">
          <PageHeader
            title="Categories"
            description="Manage your tour package categories"
            actions={
              <button className="btn btn-primary">
                <FaPlus className="h-4 w-4 mr-2" />
                Add Category
              </button>
            }
          />

          <div className="bg-white rounded-lg shadow-sm overflow-hidden">
            <div className="min-w-full divide-y divide-gray-200">
              <div className="bg-gray-50 px-6 py-3">
                <div className="grid grid-cols-12 gap-4">
                  <div className="col-span-5 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Name</div>
                  <div className="col-span-5 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Description</div>
                  <div className="col-span-2 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</div>
                </div>
              </div>

              <div className="bg-white divide-y divide-gray-200">
                {categories.map((category) => (
                  <div key={category.id} className="hover:bg-gray-50">
                    <div className="px-6 py-4 grid grid-cols-12 gap-4 items-center">
                      <div className="col-span-5">
                        <div className="flex items-center">
                          <FaList className="h-5 w-5 text-gray-400 mr-3" />
                          <span className="font-medium text-gray-900">{category.name}</span>
                        </div>
                      </div>
                      <div className="col-span-5">
                        <p className="text-sm text-gray-500 truncate">{category.description || 'No description'}</p>
                      </div>
                      <div className="col-span-2 text-right space-x-2">
                        <button className="btn btn-sm btn-secondary">
                          <FaEdit className="h-4 w-4" />
                        </button>
                        <button className="btn btn-sm btn-danger">
                          <FaTrash className="h-4 w-4" />
                        </button>
                      </div>
                    </div>
                  </div>
                ))}

                {categories.length === 0 && (
                  <div className="text-center py-12">
                    <FaList className="mx-auto h-12 w-12 text-gray-400" />
                    <h3 className="mt-2 text-sm font-medium text-gray-900">No categories</h3>
                    <p className="mt-1 text-sm text-gray-500">Get started by creating a new category.</p>
                    <div className="mt-6">
                      <button className="btn btn-primary">
                        <FaPlus className="h-4 w-4 mr-2" />
                        Add Category
                      </button>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Delete Confirmation Modal */}
      {showDeleteModal && (
        <DeleteConfirmation
          title="Delete Category"
          message={`Are you sure you want to delete "${selectedCategory.name}"? This action cannot be undone.`}
          onConfirm={() => handleDelete(selectedCategory.id)}
          onCancel={() => {
            setShowDeleteModal(false);
            setSelectedCategory(null);
          }}
        />
      )}

      {/* Add the CategoryForm */}
      {showForm && (
        <CategoryForm
          category={selectedCategory}
          onSubmit={(data) => {
            if (selectedCategory) {
              handleUpdate(selectedCategory.id, data);
            } else {
              handleCreate(data);
            }
          }}
          onClose={() => {
            setShowForm(false);
            setSelectedCategory(null);
          }}
        />
      )}
    </Layout>
  );
};

export default Categories;
