import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  FaBox, 
  FaList, 
  FaTree, 
  FaCalendarCheck, 
  FaEnvelope,
  FaChartLine,
  FaClock,
  FaExclamationCircle
} from 'react-icons/fa';
import { packageApi, bookingApi, enquiryApi } from '../services/api';
import './Dashboard.css';

const Dashboard = () => {
  const [stats, setStats] = useState({
    packages: 0,
    bookings: 0,
    enquiries: 0,
    revenue: 0
  });
  const [recentBookings, setRecentBookings] = useState([]);
  const [recentEnquiries, setRecentEnquiries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDashboardData = async () => {
      setLoading(true);
      try {
        const [packagesRes, bookingsRes, enquiriesRes] = await Promise.allSettled([
          packageApi.getAll(),
          bookingApi.getAll(),
          enquiryApi.getAll()
        ]);

        setStats({
          packages: packagesRes.status === 'fulfilled' ? packagesRes.value.data.data.length : 0,
          bookings: bookingsRes.status === 'fulfilled' ? bookingsRes.value.data.data.length : 0,
          enquiries: enquiriesRes.status === 'fulfilled' ? enquiriesRes.value.data.data.length : 0,
          revenue: bookingsRes.status === 'fulfilled' ? 
            bookingsRes.value.data.data.reduce((acc, booking) => acc + (booking.total_amount || 0), 0) : 0
        });

        setRecentBookings(bookingsRes.status === 'fulfilled' ? bookingsRes.value.data.data.slice(0, 5) : []);
        setRecentEnquiries(enquiriesRes.status === 'fulfilled' ? enquiriesRes.value.data.data.slice(0, 5) : []);
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
        setError('Failed to load dashboard data');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  if (loading) return <div className="loading">Loading dashboard...</div>;
  if (error) return <div className="error">Error: {error}</div>;

  return (
    <div className="dashboard">
      {/* Stats Overview */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon packages">
            <FaBox />
          </div>
          <div className="stat-details">
            <h3>Total Packages</h3>
            <p>{stats.packages}</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon bookings">
            <FaCalendarCheck />
          </div>
          <div className="stat-details">
            <h3>Total Bookings</h3>
            <p>{stats.bookings}</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon enquiries">
            <FaEnvelope />
          </div>
          <div className="stat-details">
            <h3>New Enquiries</h3>
            <p>{stats.enquiries}</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon revenue">
            <FaChartLine />
          </div>
          <div className="stat-details">
            <h3>Total Revenue</h3>
            <p>${stats.revenue.toLocaleString()}</p>
          </div>
        </div>
      </div>

      {/* Recent Activity */}
      <div className="activity-grid">
        {/* Recent Bookings */}
        <div className="activity-card">
          <div className="card-header">
            <h2>Recent Bookings</h2>
            <Link to="/bookings" className="view-all">View All</Link>
          </div>
          <div className="activity-list">
            {recentBookings.map(booking => (
              <div key={booking.id} className="activity-item">
                <div className="activity-icon">
                  <FaClock />
                </div>
                <div className="activity-details">
                  <h4>{booking.packageName}</h4>
                  <p>
                    {booking.customerName} - {new Date(booking.createdAt).toLocaleDateString()}
                  </p>
                  <span className={`status ${booking.status}`}>
                    {booking.status}
                  </span>
                </div>
              </div>
            ))}
            {recentBookings.length === 0 && (
              <div className="no-data">
                <FaExclamationCircle />
                <p>No recent bookings</p>
              </div>
            )}
          </div>
        </div>

        {/* Recent Enquiries */}
        <div className="activity-card">
          <div className="card-header">
            <h2>Recent Enquiries</h2>
            <Link to="/enquiries" className="view-all">View All</Link>
          </div>
          <div className="activity-list">
            {recentEnquiries.map(enquiry => (
              <div key={enquiry.id} className="activity-item">
                <div className="activity-icon">
                  <FaEnvelope />
                </div>
                <div className="activity-details">
                  <h4>{enquiry.subject}</h4>
                  <p>
                    {enquiry.name} - {new Date(enquiry.createdAt).toLocaleDateString()}
                  </p>
                  <span className={`status ${enquiry.status}`}>
                    {enquiry.status}
                  </span>
                </div>
              </div>
            ))}
            {recentEnquiries.length === 0 && (
              <div className="no-data">
                <FaExclamationCircle />
                <p>No recent enquiries</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
