import React from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import { 
  FaHome, 
  FaBox, 
  FaTree, 
  FaList, 
  FaCalendarCheck, 
  FaEnvelope, 
  FaCog,
  FaSignOutAlt
} from 'react-icons/fa';

const Sidebar = () => {
  const location = useLocation();

  const navItems = [
    { path: '/dashboard', icon: FaHome, label: 'Dashboard' },
    { path: '/packages', icon: FaBox, label: 'Packages' },
    { path: '/parks', icon: FaTree, label: 'Parks' },
    { path: '/categories', icon: FaList, label: 'Categories' },
    { path: '/bookings', icon: FaCalendarCheck, label: 'Bookings' },
    { path: '/enquiries', icon: FaEnvelope, label: 'Enquiries' },
  ];

  const isActive = (path) => location.pathname === path;

  return (
    <div className="h-screen w-64 bg-white border-r border-gray-200 flex flex-col">
      {/* Logo Section */}
      <div className="p-6 border-b border-gray-200">
        <h1 className="text-2xl font-bold text-gray-900">Safari Tours</h1>
        <p className="text-sm text-gray-500 mt-1">Management Portal</p>
      </div>

      {/* Navigation Links */}
      <nav className="flex-1 overflow-y-auto py-4">
        <div className="px-4 mb-4">
          <p className="text-xs font-semibold text-gray-400 uppercase tracking-wider">Main Menu</p>
        </div>
        {navItems.map(({ path, icon: Icon, label }) => (
          <NavLink
            key={path}
            to={path}
            className={`flex items-center px-6 py-3 text-sm font-medium transition-colors duration-200 ${
              isActive(path)
                ? 'text-blue-600 bg-blue-50 border-r-4 border-blue-600'
                : 'text-gray-600 hover:text-blue-600 hover:bg-gray-50'
            }`}
          >
            <Icon className={`h-5 w-5 mr-3 ${isActive(path) ? 'text-blue-600' : 'text-gray-400'}`} />
            {label}
          </NavLink>
        ))}
      </nav>

      {/* Bottom Section */}
      <div className="p-4 border-t border-gray-200">
        <div className="flex items-center px-2 py-3">
          <img
            src="https://ui-avatars.com/api/?name=Admin&background=0D8ABC&color=fff"
            alt="Admin"
            className="h-8 w-8 rounded-full"
          />
          <div className="ml-3">
            <p className="text-sm font-medium text-gray-900">Admin User</p>
            <p className="text-xs text-gray-500">admin@example.com</p>
          </div>
        </div>
        <div className="mt-4 space-y-2">
          <button className="w-full flex items-center px-4 py-2 text-sm font-medium text-gray-600 hover:text-blue-600 hover:bg-gray-50 rounded-md transition-colors duration-200">
            <FaCog className="h-4 w-4 mr-3" />
            Settings
          </button>
          <button className="w-full flex items-center px-4 py-2 text-sm font-medium text-red-600 hover:bg-red-50 rounded-md transition-colors duration-200">
            <FaSignOutAlt className="h-4 w-4 mr-3" />
            Sign Out
          </button>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
