import React, { useState, useEffect } from 'react';
import { 
  FaPlus, 
  FaEdit, 
  FaTrash, 
  FaSearch, 
  FaImage,
  FaSortAmountDown
} from 'react-icons/fa';
import { packageApi, categoryApi, parkApi } from '../services/api';
import PackageForm from '../components/packages/PackageForm';
import DeleteConfirmation from '../components/common/DeleteConfirmation';
import './Packages.css';

const Packages = () => {
  const [packages, setPackages] = useState([]);
  const [categories, setCategories] = useState([]);
  const [parks, setParks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [selectedPackage, setSelectedPackage] = useState(null);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('name');
  const [sortOrder, setSortOrder] = useState('asc');
  const [filteredPackages, setFilteredPackages] = useState([]);

  // Define fetchData outside useEffect so it can be reused
  const fetchData = async () => {
    setLoading(true);
    try {
      const [packagesRes, categoriesRes, parksRes] = await Promise.allSettled([
        packageApi.getAll(),
        categoryApi.getAll(),
        parkApi.getAll()
      ]);

      console.log('API Responses:', { packagesRes, categoriesRes, parksRes });

      // Safely get data with null checks and ensure arrays
      const packages = Array.isArray(packagesRes.status === 'fulfilled' ? packagesRes.value?.data?.data : null) 
        ? packagesRes.value.data.data 
        : [];
      const categories = Array.isArray(categoriesRes.status === 'fulfilled' ? categoriesRes.value?.data?.data : null)
        ? categoriesRes.value.data.data
        : [];
      const parks = Array.isArray(parksRes.status === 'fulfilled' ? parksRes.value?.data?.data : null)
        ? parksRes.value.data.data
        : [];

      console.log('Processed Data:', { packages, categories, parks });

      setPackages(packages);
      setCategories(categories);
      setParks(parks);
      setFilteredPackages(packages); // Initialize filtered packages with all packages
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Failed to load data');
      // Ensure filteredPackages is an array even on error
      setFilteredPackages([]);
    } finally {
      setLoading(false);
    }
  };

  // Update filtered packages when search term or sort options change
  useEffect(() => {
    if (!Array.isArray(packages)) {
      console.warn('Packages is not an array:', packages);
      setFilteredPackages([]);
      return;
    }

    const filtered = packages
      .filter(pkg => 
        (pkg.title?.toLowerCase() || '').includes(searchTerm.toLowerCase()) ||
        (pkg.description?.toLowerCase() || '').includes(searchTerm.toLowerCase())
      )
      .sort((a, b) => {
        const aValue = (a[sortBy] || '').toString();
        const bValue = (b[sortBy] || '').toString();
        return sortOrder === 'asc' 
          ? aValue.localeCompare(bValue)
          : bValue.localeCompare(aValue);
      });

    console.log('Filtered Packages:', filtered);
    setFilteredPackages(filtered);
  }, [packages, searchTerm, sortBy, sortOrder]);

  // Initial data fetch
  useEffect(() => {
    fetchData();
  }, []);

  const handleCreate = async (data) => {
    try {
      await packageApi.create(data);
      fetchData();
      setShowForm(false);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleUpdate = async (id, data) => {
    try {
      await packageApi.update(id, data);
      fetchData();
      setShowForm(false);
      setSelectedPackage(null);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleDelete = async (id) => {
    try {
      await packageApi.delete(id);
      fetchData();
      setShowDeleteModal(false);
      setSelectedPackage(null);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleImageUpload = async (id, formData) => {
    try {
      await packageApi.updateImage(id, formData);
      fetchData();
    } catch (err) {
      setError(err.message);
    }
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSort = (field) => {
    if (field === sortBy) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(field);
      setSortOrder('asc');
    }
  };

  if (loading) return <div className="loading">Loading packages...</div>;
  if (error) return <div className="error">Error: {error}</div>;

  return (
    <div className="packages-page">
      {/* Header */}
      <div className="page-header">
        <h1>Manage Packages</h1>
        <button 
          className="add-btn"
          onClick={() => {
            setSelectedPackage(null);
            setShowForm(true);
          }}
        >
          <FaPlus /> Add Package
        </button>
      </div>

      {/* Filters */}
      <div className="filters">
        <div className="search-box">
          <FaSearch />
          <input
            type="text"
            placeholder="Search packages..."
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>

        <div className="sort-box">
          <FaSortAmountDown />
          <select value={sortBy} onChange={(e) => handleSort(e.target.value)}>
            <option value="name">Sort by Name</option>
            <option value="price">Sort by Price</option>
          </select>
          <select value={sortOrder} onChange={(e) => setSortOrder(e.target.value)}>
            <option value="asc">Ascending</option>
            <option value="desc">Descending</option>
          </select>
        </div>
      </div>

      {/* Package List */}
      <div className="package-list">
        {loading ? (
          <div className="loading">Loading packages...</div>
        ) : error ? (
          <div className="error">{error}</div>
        ) : !Array.isArray(filteredPackages) || filteredPackages.length === 0 ? (
          <div className="no-packages">No packages found</div>
        ) : (
          <div className="package-grid">
            {filteredPackages.map(pkg => (
              <div key={pkg.id} className="package-card">
                <div className="package-image">
                  <img src={pkg.image_url} alt={pkg.title} />
                  <div className="image-overlay">
                    <label className="upload-btn">
                      <FaImage />
                      <input
                        type="file"
                        accept="image/*"
                        onChange={(e) => {
                          const formData = new FormData();
                          formData.append('image', e.target.files[0]);
                          handleImageUpload(pkg.id, formData);
                        }}
                      />
                    </label>
                  </div>
                </div>
                <div className="package-content">
                  <h3>{pkg.title}</h3>
                  <p>{pkg.description}</p>
                  <div className="package-meta">
                    <span className="price">${pkg.price.toLocaleString()}</span>
                    <span className="duration">{pkg.duration}</span>
                  </div>
                  <div className="package-categories">
                    {pkg.categories.map((cat, index) => (
                      <span key={index} className="category-tag">{cat}</span>
                    ))}
                  </div>
                  <div className="package-actions">
                    <button
                      className="edit-btn"
                      onClick={() => {
                        setSelectedPackage(pkg);
                        setShowForm(true);
                      }}
                    >
                      <FaEdit /> Edit
                    </button>
                    <button
                      className="delete-btn"
                      onClick={() => {
                        setSelectedPackage(pkg);
                        setShowDeleteModal(true);
                      }}
                    >
                      <FaTrash /> Delete
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Package Form Modal */}
      {showForm && (
        <PackageForm
          package={selectedPackage}
          categories={categories}
          parks={parks}
          onSubmit={selectedPackage ? handleUpdate : handleCreate}
          onClose={() => {
            setShowForm(false);
            setSelectedPackage(null);
          }}
        />
      )}

      {/* Delete Confirmation Modal */}
      {showDeleteModal && (
        <DeleteConfirmation
          title="Delete Package"
          message={`Are you sure you want to delete "${selectedPackage.title}"? This action cannot be undone.`}
          onConfirm={() => handleDelete(selectedPackage.id)}
          onCancel={() => {
            setShowDeleteModal(false);
            setSelectedPackage(null);
          }}
        />
      )}
    </div>
  );
};

export default Packages;
